import { Component, ElementRef, ViewChild } from '@angular/core';
import { ServiceService } from 'src/app/service/service.service';
export interface location {
  Location: string
}
export interface luggage {
  H_ID: number;
  Location: string;
  cam_ip: string;
  camera_region: number;
  cameraname: string;
  date: string;
  image_path: string;
  luggage_count: number;
  time: string;
  timestamp: string;
  _id: string;

}
@Component({
  selector: 'app-luggage-count',
  templateUrl: './luggage-count.component.html',
  styleUrls: ['./luggage-count.component.css']
})
export class LuggageCountComponent {
  @ViewChild('scrollContainer', { read: ElementRef }) scrollContainer: ElementRef | undefined;
  loationsArray: location[] = [];
  luggageArray: luggage[] = [];
  eventValue: any;
  visibleImage: boolean = false;
  imageSources: string[] = [];
  constructor(private service: ServiceService) {

  }
  ngOnInit() {
    this.getLocatioByLuggage();
  }
  getLocatioByLuggage() {
    this.service.getLocationbyLuggage().subscribe((locations: any) => {
      this.loationsArray = locations.locations
      console.log('locations',this.loationsArray);

    })
  }
  selectLocation(event: any) {
    this.eventValue = event.target.value;
    console.log('this.eventValue', this.eventValue);

    this.service.getRegionByLocation(this.eventValue).subscribe((regionData: any) => {
        this.luggageArray = regionData;
        console.log('regionData', this.luggageArray);
    });
}


  luggageImage(region: any) {
    this.visibleImage = !this.visibleImage;

    this.service.getImageforRegion(region).subscribe((images: any) => {
      console.log('images url', images.imageUrls);
      this.imageSources = images.imageUrls
    })
  }
  scrollLeft(): void {
    this.scrollContainer?.nativeElement.scrollBy({
      left: -200,
      behavior: 'smooth'
    });
  }

  scrollRight(): void {
    this.scrollContainer?.nativeElement.scrollBy({
      left: 200,
      behavior: 'smooth'
    });
  }
}
<div class="mainContainer">
    <div class="backgroundImage">
        <div class="imageText">
            <h2 style="text-align: center;">Luggage</h2>
            <span class="sentence">Analysis of Vehicle Count Across Different Categories Such as In/Out Status and Images.</span>
            <div class="row cardDiv">
                <div class="col-6 d-flex align-items-center">
                    <select id="locationSelect" class="form-select dropdownclass" (change)="selectLocation($event)">
                        <option value="">Select Region</option>
                        <option *ngFor="let location of loationsArray" [value]="location.Location">{{ location.Location }}</option>
                    </select>
                </div>
                <div class="col-6 d-flex align-items-center justify-content-end">
                    <div class="buttonClass">
                        <button type="button" class="btn btn-dark report">Download Report</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
    <div class="row maincardDiv">
        <div class="col-lg-4 col-sm-6" *ngFor="let region of luggageArray">
            <div class="card-stats card">
                <div class="card-body">
                    <div class="row">
                        <div class="col-5">
                            <img src="assets/images/bagIcon.webp" alt="Description of image" style="width: 3rem;">
                            <div>Region : 1</div>
                        </div>
                        <div class="col-7">
                            <div class="numbers">
                                <p class="card-category">Luggage Count</p>
                                <h4 class="card-title">50</h4>
                            </div>
                        </div>
                    </div>
                </div>
                <div class="card-footer">
                    <hr style="color: #000000;">
                    <div class="statsDate">
                       Date : 29 Jun 2024
                       <span class="imageclass" (click)="luggageImage(region.camera_region)">Images</span>
                    </div>
                </div>
            </div>
        </div>
    </div>
    <div class="slider-container">
        <button class="nav-button left" (click)="scrollLeft()">&#10094;</button>
        <div class="scroll-container" #scrollContainer>
            <ng-container *ngFor="let src of imageSources">
                <img [src]="src" alt="Image" class="scroll-image">
            </ng-container>
        </div>
        <button class="nav-button right" (click)="scrollRight()">&#10095;</button>
    </div>
</div>
